import React from "react";
import "./css/EditPrisoner.css";

class EditPrisoner extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      prisonerName: '',
      prisonerAvailability: null,
      prisonerPicture: '',
      prisonerProfile: '',
      prisonerSkills: '',
    };
  }

  componentDidMount() {
    const id = this.props.match.params.id;

    this.props.getPrisonerInfo(id);

    this.setState({
      prisonerName: this.props.prisonerName,
      prisonerAvailability: this.props.prisonerAvailability,
      prisonerPicture: this.props.prisonerPicture,
      prisonerProfile: this.props.prisonerProfile,
      prisonerSkills: this.props.prisonerSkills,
    })
  }

  render() {
    console.log(this.props)
    return (
      // <div className="prisoner-wrapper">
      //   <div className="summary-info">
      //     <div className="header">
      //       <h3>{this.props.prisonerName}</h3>
      //       <p>{this.props.prisonerAvailability === 1 ? "Avaiable" : "Currently Unavailable"}</p>
      //     </div>
      //     <p>{this.props.prisonerSkills}</p>
      //   </div>

      //   <div>
      //     <img src={this.props.prisonerPicture} alt="profile" />
      //     <p>{this.props.prisonerProfile}</p>
      //   </div>
      // </div>

      <div className="prisoner-wrapper">
        <input type="text" name="prisonerName" onChange={this.handleChange} value={this.state.prisonerName} placeholder={this.props.prisonerName} />
        <input  name="prisonerAvailability" onChange={this.handleChange} value={this.state.prisonerAvailability} placeholder={this.props.prisonerAvailability} />
        <input type="text" name="prisonerSkills" onChange={this.handleChange} value={this.state.prisonerSkills} placeholder={this.props.prisonerSkills} />
        <input type="text" name="prisonerProfile" onChange={this.handleChange} value={this.state.prisonerProfile} placeholder={this.props.prisonerProfile} />
      </div>
    );
  }
}

export default EditPrisoner;
